function rot13(str) { // LBH QVQ VG!
  var newArr = str.split("");
  for (var i = 0; i < newArr.length; i++) {
    var code = (str[i].charCodeAt())
    if (code >= "A".charCodeAt() && code <= "Z".charCodeAt()) {
      code = (code - "A".charCodeAt() + 13) % 26 + "A".charCodeAt();
      newArr[i] = String.fromCharCode(code);
    }
  }

  return newArr.join("");
}

